
public class iterationLoop_10th {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("\n first for loop: ");
		
		for(int i=0; i<=10;i++)
		{
			System.out.println(i);
			
		}
		
		System.out.println("\n second for loop: ");
		
		for(int a = 10; a>=0; a--)
		{
			System.out.println(a);
		}
	}

}
