
public class powerExa_7th {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double n = 2, i = 5, a;
		
		System.out.println("The number is: " + n);
		System.out.println("The power of this number is: " + i);
		
		a = Math.pow(n, i);
		
		System.out.println("The power of " + i + " of number " + n + " is: " + a);
	}

}
