
public class sixth_stringFunctions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str1 = "Java";
		String str2 = str1.concat(" Language");
		
		
		System.out.println("The original statement: " + str1);
		System.out.println("The statement after concation: " + str2);
		System.out.println("The length of the full statement: " + str2.length());

	}

}
